app.controller("chapterController",function(t,n,e){function a(){$("figure").each(function(){var t=$(this).attr("data-id");$(this).prepend("Figure "+t+" &mdash; "),$(this).wrapInner("<figcaption></figcaption>");var n="media/"+t+".jpg",e=$('<img src="'+n+'">');$(this).prepend(e)}),$(".alert-note").each(function(){$(this).prepend("<h5>NOTE</h5>")}),$(".alert-warning").each(function(){$(this).prepend("<h5>WARNING</h5>")}),$(".alert-caution").each(function(){$(this).prepend("<h5>CAUTION</h5>")})}t.startChapter=1,t.chapterID=t.startChapter,e.get("chapters.json").then(function(n){t.chapters=n.data}),t.openChapter=function(n){$(".portal").load(n+".html",function(){$(".portal").append($('<div class="quiz"></div>')),e.get("chapterQuestions.json").then(function(e){t.questions=e.data[n].questions}),a()})},t.openChapter(t.chapterID)});