app.controller("chapterController",function(t,e,n){function a(){$("figure").each(function(){var t=$(this).attr("data-id");$(this).prepend("Figure "+t+" &mdash; "),$(this).wrapInner("<figcaption></figcaption>");var e="media/"+t+".jpg",n=$('<img src="'+e+'">');$(this).prepend(n)}),$(".alert-note").each(function(){$(this).prepend("<h5>NOTE</h5>")}),$(".alert-warning").each(function(){$(this).prepend("<h5>WARNING</h5>")}),$(".alert-caution").each(function(){$(this).prepend("<h5>CAUTION</h5>")})}t.startChapter=1,t.chapterID=t.startChapter,t.openChapter=function(){console.log(t.chapterID),$(".portal").load(t.chapterID+".html",function(){$(".portal").append($('<div class="quiz"></div>')),n.get("chapterQuestions.json").then(function(e){t.questions=e.data[t.chapterID].questions}),a()})},t.openChapter()});